        if (responseJson['message'] != null) {
          alert(JSON.stringify(responseJson['message']));
        }
        clientwisetableData();
      }).catch(function (error) {
        console.error(error);
      });
    });
  };

  var updateDevice = function updateDevice(client, currenthwid) {
    setIsDialogVisible(false);
    var url = apiUrl + '/regdev/' + '' + oldClient + '';
    var putMethod = {
      method: 'PUT',
      headers: {
        'Content-type': 'application/json',
        Accept: 'application/json',
        Authorization: 'Bearer ' + Api.replace(/['"]+/g, '') + ''
      },
      body: JSON.stringify({
        hwid: oldhwid,
        nclient: client,
        nhwid: currenthwid,
        devID: devid,
        devEUI: deveui,
        deviceid: deviceid,
        datetime: datestringvalue,
        mmname: measName,
        fdname: fieldName
      })
    };
    console.log(JSON.stringify(putMethod));
    fetch(url, putMethod).then(function (response) {
      var statusCode = response.status;
      if (statusCode == 403) {
        alert('Session expired');
        Restart();
      }
      response.json().then(function (responseJson) {
        if (responseJson['message'] != null) {
          alert(JSON.stringify(responseJson));
        }
      });
    }).catch(function (error) {
      console.error(error);
    });
    clientwisetableData();
  };

  var devicedropdownenabled = function devicedropdownenabled(itemValue) {
    setdeviceoptionselected(itemValue);
    var url = apiUrl + '/getdev/' + selectedValue;
    var posetMethod = {
      method: 'POST',
      headers: {
        'Content-type': 'application/json',
        Accept: 'application/json',
        Authorization: 'Bearer ' + Api.replace(/['"]+/g, '') + ''
      },
      body: JSON.stringify({
        type: itemValue
      })
    };
    fetch(url, posetMethod).then(function (response) {
      var statusCode = response.status;
      if (statusCode == 403) {
        alert('Session expired');
        Restart();
      }
      response.json().then(function (responseJson) {
        if (responseJson['message'] != null) {
          alert(JSON.stringify(responseJson['message']));
        }
        var mesasurementdata = [];
        mesasurementdata.push("select device id");
        if (responseJson["device_list"] != undefined) {
          for (var i = 0; i < responseJson["device_list"].length; i++) {
            mesasurementdata.push(responseJson["device_list"][i]);
          }
        }
        setdeviceoptionvalue(mesasurementdata);
      });
    }).catch(function (error) {
      console.error(error);
    });
  };

