      setHardwareid(itemValue);
    } else if (deviceoptionselected == "deviceid") {
      setdeviceid(itemValue);
      setHardwareid(itemValue);
    } else {
      setdeveui(itemValue);
      setHardwareid(itemValue);
    }
  };
